INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'sv_SE', 'Var vänlig att verifiera ditt {WEB_TITLE} konto', '<p> Hej {USER_NAME}, </p> <p>Vänligen bekräfta din registrering genom att klicka på följande länk: {VALIDATION_LINK} </p> <p>Tack!</p> <p>Hälsningar, </p> <p>{WEB_LINK} </p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'sv_SE', '{WEB_TITLE} - Registreringen lyckades!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'sv_SE', 'Kolla vad jag hittade på {WEB_TITLE}', '<p>Hej {FRIEND_NAME},</p><p>Din vän {USER_NAME} vill dela denna annons med dig <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Meddelande:</p><p>{COMMENT}</p><p>Hälsningar,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'sv_SE', '{WEB_TITLE} - Nya annonser in the last hour', '<p>Hej {USER_NAME},</p><p>Nya annonser har publicerats senaste timman. Ta en titt på dem:</p><p>{ADS}</p><p>-------------</p><p>För att avsluta prenumerationen på den här bevakningen, klicka på: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'sv_SE', '{WEB_TITLE} - Nya annonser in the last day', '<p>Hej {USER_NAME},</p><p>Nya annonser har publicerats den senaste dagen. Ta en titt på dem:</p><p>{ADS}</p><p>-------------</p><p>För att avsluta prenumerationen på den här bevakningen, klicka på: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'sv_SE', '{WEB_TITLE} - Nya annonser in the last week', '<p>Hi {USER_NAME},</p><p>Nya annonser har publicerats den senaste veckan. Ta en titt på dem:</p><p>{ADS}</p><p>-------------</p><p>För att avsluta prenumerationen på den här bevakningen, klicka på: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'sv_SE', '{WEB_TITLE} - Nya annonser', '<p>Hej {USER_NAME},</p><p>En ny annons har blivit publicerad, Kolla in den!</p><p>{ADS}</p><p>-------------</p><p>För att avregistrera dig från den här notifieringen, Klicka på: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'sv_SE', '{WEB_TITLE} - Ny kommentar', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'sv_SE', '{WEB_TITLE} - Redigera alternativ för annons {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'sv_SE', '{WEB_TITLE} - Verifiera din annons', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'sv_SE', '{WEB_TITLE} - En ny annons har publicerats', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'sv_SE', '{WEB_TITLE} - Återställ ditt lösenord', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'sv_SE', '{WEB_TITLE} - Du begärde att ändra din e-post', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'sv_SE', '{WEB_TITLE} - Validera din varning', '<p>Hej {USER_NAME},</p><p>Var vänlig bekräfta din uppdateringssregistrering genom att klicka på följande länk: {VALIDATION_LINK}</p><p>Tack!</p><p>Med vänliga hälsningar,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'sv_SE', '{WEB_TITLE} - Din kommentar har blivit godkännd', '<p>Hej {COMMENT_AUTHOR},</p><p>Din kommentar på <a href="{ITEM_URL}">{ITEM_TITLE}</a> har godkänts.</p><p>Hälsningar,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'sv_SE', '{WEB_TITLE} - Verifiera din annons', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'sv_SE', '{WEB_TITLE} - En ny användare har registrerats', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'sv_SE', '{WEB_TITLE} - Någon har en fråga till dig', '<p>Hej {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) Har lämnat ett meddelande till dig:</p><p>{COMMENT}</p><p>Hälsningar,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'sv_SE', '{WEB_TITLE} - Någon har kommenterat din annons', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'sv_SE', '{WEB_TITLE} - Lyckades skapa admin konto', '<p>Hej {ADMIN_NAME},</p><p>Administratören för {WEB_LINK} har skapat ett konto för dig,</p><ul><li>Användarnamn: {USERNAME}</li><li>Lösenord: {PASSWORD}</li></ul><p>Du kan komma åt administratörspanelen här {WEB_ADMIN_LINK}.</p><p>Tack!</p><p>Med vänliga hälsningar,</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (22, 'sv_SE', '{WEB_TITLE} - Din annons håller på att gå ut', '<p>Hej {USER_NAME},</p><p>Din annons <a href="{ITEM_URL}">{ITEM_TITLE}</a> håller på att gå ut {WEB_LINK}.');
